import { useState } from "react";
import {
  StyleSheet,
  View,
  ScrollView,
  Platform,
  Text as NText,
  SafeAreaView,
} from "react-native";

import {
  Stack,
  Input,
  Icon,
  FormControl,
  HStack,
  Radio,
  Select,
  Button,
  useToast,
  Text,
} from "native-base";
import { Ionicons } from "@expo/vector-icons";
import CountryPicker from 'react-native-country-picker-modal'
import { Buttons, MyKeyboardAvoidingView } from "../../../common";
import { AntDesign } from '@expo/vector-icons';

export const StepOne = ({ navigation, setActiveStep, form }) => {

  const toast = useToast();
  const [countryCode, setCountryCode] = useState('US')
  const [country, setCountry] = useState(null)
  const [withCountryNameButton, setWithCountryNameButton] = useState(
    false,
  )
  const [withFlag, setWithFlag] = useState(true)
  const [withEmoji, setWithEmoji] = useState(true)
  const [withFilter, setWithFilter] = useState(true)
  const [withAlphaFilter, setWithAlphaFilter] = useState(false)
  const [withCallingCode, setWithCallingCode] = useState(false)



  const onSelect = (country) => {
    setCountryCode(country.cca2)
    setCountry(country)
  }

  let [service, setService] = useState("");

  const handleSubmit = () => {
    // if (form["name"] === "") {
    //   toast.show({
    //     title: "Name can not be empty.",
    //     placement: "top",
    //     status: "error",
    //   });
    // } else {
    //   setActiveStep(2);
    // }

    setActiveStep(2);
  };

  return (
    <SafeAreaView>
      <ScrollView>


        <View style={{ paddingBottom:20 }} >
          <HStack
            alignItems={"center"}
            justifyContent={"center"}
            style={{
              marginBottom: 30,
              marginTop: Platform === "ios" ? 95 : 30,
              paddingHorizontal: 15,
            }}
          >
            <NText style={styles.screenTitle}>{"Basic Info"}</NText>
          </HStack>


          <Stack
            space={4}
            w="100%"
            alignItems="center"
            style={{ paddingHorizontal: 15, marginBottom: 40 }}
          >



            <FormControl>
              <FormControl.Label>Name</FormControl.Label>
              <Input
                w={{
                  base: "100%",
                  md: "100%",
                }}
                h={12}
                variant="Filled"
                bg={"#333333"}
                InputLeftElement={
                  <Icon
                    as={<Ionicons name="person-outline" />}
                    size={5}
                    ml="2"
                    color="muted.400"
                  />
                }
                placeholder="Enter name"
              />
            </FormControl>
            <FormControl>
              <FormControl.Label>Desired user name</FormControl.Label>
              <Input
                w={{
                  base: "100%",
                  md: "100%",
                }}
                h={12}
                variant="Filled"
                bg={"#333333"}
                style={{ color: "#ffffff" }}
                InputRightElement={
                  <Icon
                    as={<Ionicons name="chevron-down-outline" />}
                    size={4}
                    mr="2"
                    color="muted.400"
                  />
                }
                placeholder="mike09_7"
              />
            </FormControl>
            <FormControl>
              <FormControl.Label>Email</FormControl.Label>
              <Input
                w={{
                  base: "100%",
                  md: "100%",
                }}
                h={12}
                variant="Filled"
                bg={"#333333"}
                style={{ color: "#ffffff" }}
                InputLeftElement={
                  <Icon
                    as={<Ionicons name="mail-outline" />}
                    size={5}
                    ml="2"
                    color="muted.400"
                  />
                }
                placeholder="Enter email"
              />
            </FormControl>

            <FormControl>
              <HStack space={3} justifyContent="flex-start">
                <FormControl.Label>Address</FormControl.Label>
                <AntDesign name="edit" size={24} color="#333333" />
              </HStack>
              <HStack space={3} justifyContent="space-between" w="100%">
                <Stack flex={1}>
                  {/* <Input
                    h={12}
                    w="auto"
                    variant="Filled"
                    bg={"#333333"}
                    style={{ color: "#ffffff" }}
                    InputLeftElement={
                      <Icon
                        as={<Ionicons name="flag-outline" />}
                        size={5}
                        ml="2"
                        color="muted.400"
                      />
                    }
                    placeholder="USA"
                  /> */}
                  <View style={{ flex: 1, flexDirection: "row", alignItems: "center", backgroundColor: "#333", borderRadius: 5, paddingLeft: 10 }}>
                    <CountryPicker
                      {...{
                        countryCode,
                        withFilter,
                        withFlag,
                        withCountryNameButton,
                        withAlphaFilter,
                        withCallingCode,
                        withEmoji,
                        onSelect,
                      }}
                      visible={false}
                    />
                    <Text style={{ color: "#fff" }}>{!country?.name ? "Usa" : country?.name}</Text>

                  </View>
                </Stack>
                <Stack flex={1}>
                  <Input
                    h={12}
                    w="auto"
                    variant="Filled"
                    bg={"#333333"}
                    style={{ color: "#ffffff" }}
                    placeholder="California"
                  />
                </Stack>
              </HStack>
              <Stack mt={2}>
                <Input
                  h={12}
                  w="auto"
                  variant="Filled"
                  bg={"#333333"}
                  style={{ color: "#ffffff" }}
                  InputLeftElement={
                    <Icon
                      as={<Ionicons name="location-outline" />}
                      size={5}
                      ml="2"
                      color="muted.400"
                    />
                  }
                  placeholder="1220 mt road, block 2"
                />
              </Stack>
            </FormControl>

            <FormControl>
              <FormControl.Label>Gender</FormControl.Label>
              <Radio.Group
                name="exampleGroup"
                defaultValue="1"
                accessibilityLabel="pick a size"
              >
                <HStack
                  direction={{
                    base: "row",
                    md: "row",
                  }}
                  space={4}
                >
                  <Radio value="1" colorScheme="blue" size="md" my={1}>
                    <Text color="#fff">Male</Text>
                  </Radio>
                  <Radio value="2" colorScheme="blue" size="md" my={1}>
                    <Text color="#fff">Female</Text>
                  </Radio>
                  <Radio value="3" colorScheme="blue" size="md" my={1}>
                    <Text color="#fff">Not to say</Text>
                  </Radio>
                </HStack>
              </Radio.Group>
            </FormControl>

            <FormControl>
              <FormControl.Label>Age</FormControl.Label>
              <HStack space={3} justifyContent="space-between" w="100%">
                <Stack flex={1}>
                  <Input
                    h={12}
                    w="auto"
                    variant="Filled"
                    bg={"#333333"}
                    style={{ color: "#ffffff" }}
                    InputRightElement={
                      <Icon
                        as={<Ionicons name="calendar-outline" />}
                        size={4}
                        mr="2"
                        color="muted.400"
                      />
                    }
                    placeholder="Month"
                  />
                </Stack>
                <Stack flex={1}>
                  <Input
                    h={12}
                    w="auto"
                    variant="Filled"
                    bg={"#333333"}
                    style={{ color: "#ffffff" }}
                    InputRightElement={
                      <Icon
                        as={<Ionicons name="calendar-outline" />}
                        size={4}
                        mr="2"
                        color="muted.400"
                      />
                    }
                    placeholder="Day"
                  />
                </Stack>
                <Stack flex={1}>
                  <Input
                    h={12}
                    w="auto"
                    variant="Filled"
                    bg={"#333333"}
                    style={{ color: "#ffffff" }}
                    InputRightElement={
                      <Icon
                        as={<Ionicons name="calendar-outline" />}
                        size={4}
                        mr="2"
                        color="muted.400"
                      />
                    }
                    placeholder="Year"
                  />
                </Stack>
              </HStack>
            </FormControl>

            <FormControl>
              <FormControl.Label>
                Ethincity{" "}
                <Ionicons
                  name="information-circle"
                  size={18}
                  color={"#9A9FA5"}
                />
              </FormControl.Label>
              <Select
                h={12}
                selectedValue={service}
                minWidth="200"
                accessibilityLabel="Choose one"
                placeholder="Choose one"
                _selectedItem={{
                  bg: "teal.600",
                  endIcon: <Ionicons name="chevron-down-outline" size={4} />,
                }}
                mt={1}
                variant="Filled"
                bg={"#333333"}
                style={{ color: "#ffffff" }}
                onValueChange={(itemValue) => setService(itemValue)}
              >
                <Select.Item key={1} label="UX Research" value="ux" />
                <Select.Item key={2} label="Web Development" value="web" />
              </Select>
            </FormControl>
            <FormControl>
              <FormControl.Label>
                Education Lavel{" "}
                <Ionicons
                  name="information-circle"
                  size={18}
                  color={"#9A9FA5"}
                />
              </FormControl.Label>
              <Select
                h={12}
                selectedValue={service}
                minWidth="200"
                accessibilityLabel="Choose one"
                placeholder="Choose one"
                _selectedItem={{
                  bg: "teal.600",
                  endIcon: <Ionicons name="chevron-down-outline" size={4} />,
                }}
                mt={1}
                variant="Filled"
                bg={"#333333"}
                style={{ color: "#ffffff" }}
                onValueChange={(itemValue) => setService(itemValue)}
              >
                <Select.Item key={1} label="UX Research" value="ux" />
                <Select.Item key={2} label="Web Development" value="web" />
              </Select>
            </FormControl>
            <FormControl>
              <FormControl.Label>
                Income Lavel{" "}
                <Ionicons
                  name="information-circle"
                  size={18}
                  color={"#9A9FA5"}
                />
              </FormControl.Label>
              <Select
                h={12}
                selectedValue={service}
                minWidth="200"
                accessibilityLabel="Choose one"
                placeholder="Choose one"
                _selectedItem={{
                  bg: "teal.600",
                  endIcon: <Ionicons name="chevron-down-outline" size={4} />,
                }}
                mt={1}
                variant="Filled"
                bg={"#333333"}
                style={{ color: "#ffffff" }}
                onValueChange={(itemValue) => setService(itemValue)}
              >
                <Select.Item key={1} label="UX Research" value="ux" />
