import {
  View,
  Text,
  StyleSheet,
  Image,
  Button,
  TouchableNativeFeedback,
  TouchableOpacity,
  Platform,
  ScrollView
} from "react-native";
import React from "react";
import { StatusBar } from "expo-status-bar";
import { Slider, Stack } from "native-base";
import { Buttons } from "../../common";
import { FontAwesome5 } from "@expo/vector-icons";
import { ArtistMoreHeader } from "./components/artistmoreheader";

const amount = [0, 100, 500, 1000, 2000, 3000, 4000];

export const ArtistPageSendFanbitScreenOne = ({ navigation }) => {
  const [onChangeValue, setOnChangeValue] = React.useState(20);
  const [onChangeEndValue, setOnChangeEndValue] = React.useState(20);

  return (
    <View style={styles.container}>
      <StatusBar hidden />
      <ArtistMoreHeader navigation={navigation} destination={"artistMore"} />

      <ScrollView>
        <View>
          <Text style={styles.title}>Transfer FanBit {"\n"} to Rate Talent</Text>
          <Image
            style={styles.fitbitToken}
            source={require("../../common/assets/images/artistlist/fitbit-token.png")}
          />
        </View>
        <Text style={styles.sendTo}>Send To</Text>
        <View style={styles.sendButtoncontainer}>
          <TouchableNativeFeedback>
            <Text style={styles.artistBtn}>Artist</Text>
          </TouchableNativeFeedback>
          <TouchableNativeFeedback>
            <Text style={styles.Influential}>Influential</Text>
          </TouchableNativeFeedback>
        </View>
        <View>
          <Text style={styles.sendTo}>FanBit Amount</Text>
          <Stack space={4} style={{ position: "relative", marginTop: 20 }}>
            <Text
              style={{
                color: "white",
                position: "absolute",
                left: Platform.OS === 'ios' ? onChangeValue * 2.8 : onChangeValue * 2.4,
                top: -5
              }}
              textAlign="center"
            >
              {onChangeValue}%
            </Text>
            <Slider
              width="80%"
              size="lg"
              defaultValue={70}
              colorScheme="green"
              onChange={(v) => {
                setOnChangeValue(Math.floor(v));
              }}
              onChangeEnd={(v) => {
                v && setOnChangeEndValue(Math.floor(v));
              }}
            >
              <Slider.Track bg="green.100">
                <Slider.FilledTrack bg="blue.400" />
              </Slider.Track>
              <Slider.Thumb>
                <View style={{ backgroundColor: "#49C3EB", height: 24, width: 24, borderRadius: 12, display: "flex", alignItems: "center", justifyContent: "center" }}>
                  <FontAwesome5 name="check-circle" size={18} color="white" />
                </View>
              </Slider.Thumb>
            </Slider>
            <View
              style={{
                position: "absolute",
                right: 0,
                top: 14,
                backgroundColor: "#2D6A7E",
                paddingHorizontal: 5,
                paddingVertical: 3,
                borderRadius: 3,
                fontSize: 12,
                fontWeight: "bold",
              }}
            >
              <Text style={{ color: "white" }} >{onChangeValue * 40}</Text>
            </View>
          </Stack>
          <View style={{ flexDirection: "row" }}>
            {amount.map((i) => (
              <Text key={i} style={{ color: "white", marginRight: Platform.OS === 'ios' ? 14 : 8 }}> {i} </Text>
            ))}
          </View>
        </View>
        <View style={styles.buttonGroup}>
          <Buttons onPress={() => navigation.navigate("SendFanBitTwo")} title={"Send FanBit"} fillBtn={true} />
          <Buttons onPress={() => navigation.goBack()} title={"Cancel"} outLine={true} />
        </View>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#000",
    paddingHorizontal: 20,
  },
  title: {
    color: "white",
    fontSize: 28,
    alignSelf: "center",
    marginTop: 40,
  },
  fitbitToken: {
    height: 60,
    width: 60,
    alignSelf: "center",
    marginVertical: 10,
  },
  sendTo: {
    color: "white",
    fontSize: 18,
    textAlign: "center",
    marginTop: 25,
  },
  artistBtn: {
    backgroundColor: "#378EF0",
    color: "white",
    fontSize: 16,
    borderRadius: 30,
    width: 125,
    paddingVertical: 14,
    textAlign: "center",
  },
  Influential: {
    borderColor: "#fff",
    borderWidth: 1,
    color: "white",
    fontSize: 16,
    borderRadius: 30,
    width: 125,
    paddingVertical: 14,
    textAlign: "center",
  },
  sendButtoncontainer: {
    flexDirection: "row",
    justifyContent: "space-evenly",
    marginTop: 15,
  },
  buttonGroup: {
    marginTop: 80,
  },
});
